import { useParams, useNavigate, Link } from "react-router-dom"
import { useState, useEffect } from "react";
import axios from "axios";

export const OneStore = (props) =>{
  const { id } = useParams();
  const navigate = useNavigate();

  const [store, setStore] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/stores/${id}`)
      .then((res) => {
        setStore(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  if (store === null) {
    return <h1>Loading...Pleast Wait!</h1>;
  }

const { _id, name, number, open } = store;

  return (
    <div className="w-75 p-4 mx-auto">
      <div className="shadow mb-4 rounded border p-4 text-start">
        <h2 >{name}</h2>
        <p>Store Number: {number}</p>
        {open && <p>Open</p>}
      </div>
    </div>
  )
}
